    , CODE_GRP_DESC
    , SORT_ORD
    , USE_YN
    , REG_ID
    , REG_DATE
)
VALUES
(
      #{codeGrpId}
    , #{codeGrpNm}
    , #{codeGrpEngNm}
    , #{codeGrpDesc}
    , #{sortOrd}
    , #{useYn}
    , 'admin'
    , SYSDATE
)

mapper.update:
