mapper.select:
SELECT COUNT(*)
FROM TP_COMM_CODE
WHERE 1=1
AND CODE_GRP_ID = #{selectedGrpId}

    AND ${searchOpt} LIKE CONCAT(CONCAT('%', #{searchTxt}), '%')


    AND USE_YN = #{searchUseYn}

SELECT * FROM( SELECT ROWNUM AS rn , x.* FROM( SELECT CODE_ID , CODE_GRP_ID , CODE_NM , CODE_ENG_NM , UPP_CODE_ID , CODE_LVL , CODE_DESC , SORT_ODR , RESV1 , RESV2 , RESV3 , USE_YN , UPD_ID , UPD_DATE , REG_ID , REG_DATE FROM TP_COMM_CODE WHERE 1=1 AND CODE_GRP_ID = #{comCodeListParamDto.selectedGrpId}
AND ${comCodeListParamDto.searchOpt} LIKE CONCAT(CONCAT('%', #{comCodeListParamDto.searchTxt}), '%')
AND USE_YN = #{comCodeListParamDto.searchUseYn}
ORDER BY ${comCodeListParamDto.sortBy} ASC
ORDER BY ${comCodeListParamDto.sortBy} DESC
) x ) WHERE rn BETWEEN #{condition.start} AND #{condition.end}

SELECT COUNT(*)
  FROM TP_COMM_CODE
 WHERE CODE_GRP_ID = #{codeGrpId}
   AND CODE_ID = #{codeId}

