mapper.select:
SELECT COUNT(*)
  FROM TP_API_LOG
 WHERE 1=1

   AND ACCESS_TIME >= #{dateFrom}
   AND ACCESS_TIME < #{dateTo}


    AND USER_ID = #{userId}

SELECT * FROM ( SELECT ROWNUM AS rn , x.* FROM( SELECT MENU_ID , GUBUN , GUBUN_DET , SQL_TXT , USER_ID , ACCESS_TIME , PARAMS FROM TP_API_LOG WHERE 1=1
AND ACCESS_TIME >= #{logAPIListParamsDto.dateFrom} AND ACCESS_TIME < #{logAPIListParamsDto.dateTo}
USER_ID = #{logAPIListParamsDto.userId}
ORDER BY ${logAPIListParamsDto.sortBy} ASC
ORDER BY ${logAPIListParamsDto.sortBy} DESC
) x ) WHERE rn BETWEEN #{condition.start} AND #{condition.end}
