mapper.select:
SELECT COUNT(*) FROM TP_COM_ROLE WHERE 1=1
AND ${searchOpt} LIKE CONCAT(CONCAT('%', #{searchTxt}), '%')
AND USE_YN = #{searchUseYn}

SELECT * FROM( SELECT ROWNUM AS rn , x.* FROM( SELECT ROLE_ID , ROLE_NM , ROLE_DESC , USE_YN , UPT_ID , UPT_DATE , REG_ID , REG_DATE FROM TP_COM_ROLE WHERE 1=1
AND ${roleListParamsDto.searchOpt} LIKE CONCAT(CONCAT('%', #{roleListParamsDto.searchTxt}), '%')
AND USE_YN = #{roleListParamsDto.searchUseYn}
ORDER BY ${roleListParamsDto.sortBy} ASC
ORDER BY ${roleListParamsDto.sortBy} DESC
) x ) WHERE rn BETWEEN #{condition.start} AND #{condition.end}

SELECT COUNT(*)
  FROM TP_COM_ROLE
 WHERE ROLE_ID = #{roleId}

mapper.insert:
INSERT INTO TP_COM_ROLE
(
      ROLE_ID
    , ROLE_NM
    , ROLE_DESC
    , USE_YN
    , REG_ID
    , REG_DATE
)
VALUES
(
      #{roleId}
    , #{roleNm}
    , #{roleDesc}
    , #{useYn}
    , 'admin'
    , SYSDATE
)

mapper.update:
UPDATE TP_COM_ROLE
   SET
         ROLE_NM = #{roleNm}
       , ROLE_DESC = #{roleDesc}
       , USE_YN = #{useYn}
       , UPT_ID = 'admin2'
       , UPT_DATE = SYSDATE
 WHERE ROLE_ID = #{roleId}

mapper.select:
SELECT COUNT(*)
  FROM TP_USER_ROLE
 WHERE 1=1
    AND ROLE_ID = #{selectedRoleId}

        AND USER_ID LIKE CONCAT(CONCAT('%', #{searchTxt}), '%')


        AND USE_YN = #{searchUseYn}

SELECT * FROM( SELECT ROWNUM AS rn , x.* FROM( SELECT USER_ID , ROLE_ID , USE_YN , UPT_ID , UPT_DATE , REG_ID , REG_DATE FROM TP_USER_ROLE WHERE 1=1 AND ROLE_ID = #{userRoleListParamsDto.selectedRoleId}
AND USER_ID LIKE CONCAT(CONCAT('%', #{userRoleListParamsDto.searchTxt}), '%')
AND USE_YN = #{userRoleListParamsDto.searchUseYn}
ORDER BY ${userRoleListParamsDto.sortBy} ASC
ORDER BY ${userRoleListParamsDto.sortBy} DESC
) x ) WHERE rn BETWEEN #{condition.start} AND #{condition.end}

SELECT COUNT(*)
  FROM TP_USER_ROLE
 WHERE ROLE_ID = #{roleId}
   AND USER_ID = #{userId}

mapper.insert:
INSERT INTO TP_USER_ROLE
(
      USER_ID
    , ROLE_ID
    , USE_YN
    , REG_ID
    , REG_DATE
)
VALUES
(
      #{userId}
    , #{roleId}
    , #{useYn}
    , 'admin'
    , SYSDATE
)

mapper.update:
UPDATE TP_USER_ROLE
   SET
          USE_YN = #{useYn}
        , UPT_ID = 'admin2'
        , UPT_DATE = SYSDATE
 WHERE USER_ID = #{userId}
   AND ROLE_ID = #{roleId}

UPDATE TP_USER_ROLE
   SET USE_YN = 'N'
 WHERE ROLE_ID = #{roleId}
   AND USER_ID IN

        #{item}
