mapper.select:
SELECT * FROM( SELECT ROWNUM AS rn , USER_ID , USER_NM , DEPT_CD AS deptNm , JIKGYUB , ROLE , EMAIL , TEL_NO , GENDER , USE_YN , REG_ID , REG_DATE , UPT_ID , UPT_DATE FROM( SELECT USER_ID , USER_NM , DEPT_CD , JIKGYUB , ROLE , EMAIL , TEL_NO , GENDER , USE_YN , REG_ID , TO_CHAR(REG_DATE, 'YYYY-MM-DD') AS REG_DATE , UPT_ID , TO_CHAR(UPT_DATE, 'YYYY-MM-DD') AS UPT_DATE FROM TP_USER_INFO WHERE 1=1
AND USER_ID LIKE CONCAT(CONCAT('%', #{userListParamsDto.userId}), '%')
AND USER_NM LIKE CONCAT(CONCAT('%', #{userListParamsDto.userNm}), '%')
AND USE_YN = #{userListParamsDto.useYn}
ORDER BY ${userListParamsDto.sortBy} ASC
ORDER BY ${userListParamsDto.sortBy} DESC
) ) WHERE rn BETWEEN #{condition.start} AND #{condition.end}

SELECT COUNT(*)
  FROM TP_USER_INFO
 WHERE USER_ID = #{userId}

mapper.insert:
INSERT INTO TP_USER_INFO
