, JIKGYUB
, ROLE
, EMAIL
, TEL_NO
, GENDER
, USE_YN
, REG_ID
, REG_DATE
, PASSWORD
)
VALUES
(
  #{userId}
, #{userNm}
, #{deptNm}
, #{jikgyub}
, #{role}
, #{email}
, #{telNo}
, #{gender}
, #{useYn}
, 'admin'
, SYSDATE
, #{password}
